mapper.update:
update fusion_resource
resource_name = #{resourceName}, resource_desc = #{resourceDesc}, resource_type = #{resourceType}, resource_auth_type = #{resourceAuthType}, resource_rows_count = #{resourceRowsCount}, resource_column_count = #{resourceColumnCount}, resource_column_name_list = #{resourceColumnNameList}, resource_contains_y = #{resourceContainsY}, resource_y_rows_count = #{resourceYRowsCount}, resource_y_ratio = #{resourceYRatio}, resource_tag = #{resourceTag}, organ_id = #{organId}, u_time = now()
where id = #{id}

mapper.select:
select distinct r.id,resource_id resourceId,resource_name resourceName,resource_desc resourceDesc,resource_type resourceType, resource_auth_type resourceAuthType,resource_rows_count resourceRowsCount,resource_column_count resourceColumnCount, resource_column_name_list resourceColumnNameList,resource_contains_y resourceContainsY,resource_y_rows_count resourceYRowsCount, resource_y_ratio resourceYRatio,resource_tag resourceTag,organ_id organId,r.is_del isDel,r.c_time cTime,r.u_time uTime from fusion_resource r left join fusion_go go on go.organ_global_id =r.organ_id where go.group_id in
#{item}
and (r.resource_auth_type=1 or r.organ_id=#{globalId} or (r.resource_auth_type=3 and (select count(1) from fusion_resource_visibility_auth fsva where fsva.resource_id=r.resource_id and fsva.organ_global_id=#{globalId} )=1 )) and r.is_del=0
order by r.id desc limit #{offset},#{pageSize}

select count(1) from fusion_resource r left join fusion_go go on go.organ_global_id =r.organ_id where go.group_id in
#{item}
and (r.resource_auth_type=1 or (r.resource_auth_type=3 and (select count(1) from fusion_resource_visibility_auth fsva where fsva.resource_id=r.resource_id and fsva.organ_global_id=#{globalId} )=1 )) and r.is_del=0
