mapper.select:
select project_id projectId,count(model_id) modeNum from data_model where project_id in
#{item}
GROUP BY project_id

SELECT component_id as componentId, model_id as modelId, component_code as componentCode, component_name as componentName, shape, width, height, coordinate_y as coordinateY, coordinate_x as coordinateY , data_json as dataJson, component_state as componentState, start_time as startTime, end_time as endTime FROM data_component
and model_id = #{modelId}
and component_code = #{componentCode}

select model_id as modelId, component_json as componentJson,
       model_desc as modelDesc,model_desc as modelDesc
from data_model where


            model_id = #{modelId}


            user_id = #{userId}

                and is_draft = #{isDraft}



LIMIT 1
